require 'rails_helper'

RSpec.describe Note, type: :model do
  it 'requires some text' do
    note = Note.new(text: '')
    note.valid?
    expect(note.errors[:text].any?).to eq(true)
  end
  it 'must have 5 or more characters' do
    note = Note.new(text: 'asdf')
    note.valid?
    expect(note.errors[:text].any?).to eq(true)
  end
  it 'must have 500 or fewer characters' do
    note = Note.new(text: '')
    note.valid?
    expect(note.errors[:text].any?).to eq(true)
  end
end
